import React, { useState, useContext } from 'react';
import { ChatContext } from '../../contexts/ChatContext';
import { AuthContext } from '../../contexts/AuthContext';

const Message = ({ message, isOwnMessage }) => {
  const { handleAddReaction, handleReplyToMessage } = useContext(ChatContext);
  const { currentUser } = useContext(AuthContext);
  const [showReactionMenu, setShowReactionMenu] = useState(false);
  const [replyContent, setReplyContent] = useState('');
  const [showReplyInput, setShowReplyInput] = useState(false);

  // Formater l'heure du message
  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  // V√©rifier si l'utilisateur courant a d√©j√† ajout√© une r√©action sp√©cifique
  const hasUserReacted = (reactionType) => {
    return message.reactions && message.reactions.some(
      r => r.userId === currentUser.id && r.reaction === reactionType
    );
  };

  // G√©rer l'ajout d'une r√©action
  const onAddReaction = (reaction) => {
    if (!hasUserReacted(reaction)) {
      handleAddReaction(message.id, reaction);
    }
    setShowReactionMenu(false);
  };

  // G√©rer l'envoi d'une r√©ponse
  const onSendReply = () => {
    if (replyContent.trim()) {
      handleReplyToMessage(message.id, replyContent);
      setReplyContent('');
      setShowReplyInput(false);
    }
  };

  // Compter les occurrences de chaque type de r√©action
  const getReactionCounts = () => {
    const counts = {};
    if (message.reactions) {
      message.reactions.forEach(r => {
        counts[r.reaction] = (counts[r.reaction] || 0) + 1;
      });
    }
    return counts;
  };

  const reactionCounts = getReactionCounts();

  return (
    <div className={`message-wrapper ${isOwnMessage ? 'own-message' : ''}`}>
      <div className="message">
        {/* Si c'est un message en r√©ponse, montrer le message parent */}
        {message.parentId && message.parentMessage && (
          <div className="replied-message">
            <p>{message.parentMessage.content}</p>
          </div>
        )}

        <div className="message-content">
          <p>{message.content}</p>
        </div>

        <div className="message-meta">
          <span className="message-time">{formatTime(message.timestamp)}</span>
          {isOwnMessage && message.status && (
            <span className="message-status">{message.status}</span>
          )}
        </div>

        {/* Afficher les r√©actions existantes */}
        {message.reactions && message.reactions.length > 0 && (
          <div className="reactions-container">
            {Object.entries(reactionCounts).map(([reaction, count]) => (
              <div 
                key={reaction} 
                className={`reaction ${hasUserReacted(reaction) ? 'user-reacted' : ''}`}
              >
                <span className="reaction-emoji">{reaction}</span>
                <span className="reaction-count">{count}</span>
              </div>
            ))}
          </div>
        )}

        {/* Menu des actions de message */}
        <div className="message-actions">
          <button 
            className="action-button reply"
            onClick={() => setShowReplyInput(!showReplyInput)}
            title="R√©pondre"
          >
            ‚Ü©Ô∏è
          </button>
          <button 
            className="action-button react"
            onClick={() => setShowReactionMenu(!showReactionMenu)}
            title="R√©agir"
          >
            üòä
          </button>
        </div>

        {/* Menu des r√©actions */}
        {showReactionMenu && (
          <div className="reaction-menu">
            <button onClick={() => onAddReaction('üëç')} className="reaction-button">üëç</button>
            <button onClick={() => onAddReaction('‚ù§Ô∏è')} className="reaction-button">‚ù§Ô∏è</button>
            <button onClick={() => onAddReaction('üòÑ')} className="reaction-button">üòÑ</button>
            <button onClick={() => onAddReaction('üòÆ')} className="reaction-button">üòÆ</button>
            <button onClick={() => onAddReaction('üò¢')} className="reaction-button">üò¢</button>
            <button onClick={() => onAddReaction('üëè')} className="reaction-button">üëè</button>
          </div>
        )}

        {/* R√©ponses au message */}
        {message.replies && message.replies.length > 0 && (
          <div className="message-replies">
            {message.replies.map((reply, index) => (
              <div key={index} className="reply-message">
                <div className="reply-content">
                  <p>{reply.content}</p>
                </div>
                <div className="reply-meta">
                  <span className="reply-time">{formatTime(reply.timestamp)}</span>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Input pour r√©pondre au message */}
        {showReplyInput && (
          <div className="reply-input-container">
            <input
              type="text"
              className="reply-input"
              placeholder="R√©pondre √† ce message..."
              value={replyContent}
              onChange={(e) => setReplyContent(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && onSendReply()}
            />
            <button className="send-reply-button" onClick={onSendReply}>
              Envoyer
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Message;
