import React, { createContext, useState, useEffect, useContext } from 'react';
import { AuthContext } from './AuthContext';

export const ChatContext = createContext();

export const ChatProvider = ({ children }) => {
  const { currentUser } = useContext(AuthContext);
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  // Simuler des utilisateurs en ligne
  useEffect(() => {
    if (currentUser) {
      // Exemple d'utilisateurs en ligne
      const mockOnlineUsers = [
        { id: 'user1', username: 'alice' },
        { id: 'user2', username: 'bob' },
        { id: 'user3', username: 'charlie' }
      ].filter(user => user.id !== currentUser.id);

      setOnlineUsers(mockOnlineUsers);
    }
  }, [currentUser]);

  // Charger des messages exemple quand un utilisateur est sÃ©lectionnÃ©
  useEffect(() => {
    if (selectedUser) {
      fetchMessages(selectedUser.id);
    }
  }, [selectedUser]);

  // Fonction simulÃ©e pour rÃ©cupÃ©rer les utilisateurs
  const fetchUsers = async () => {
    try {
      // Simuler un dÃ©lai rÃ©seau
      await new Promise(resolve => setTimeout(resolve, 300));

      // Exemple d'utilisateurs
      const mockUsers = [
        { id: 'user1', username: 'alice' },
        { id: 'user2', username: 'bob' },
        { id: 'user3', username: 'charlie' },
        { id: 'user4', username: 'david' },
        { id: 'user5', username: 'emma' }
      ].filter(user => user.id !== currentUser?.id);

      return mockUsers;
    } catch (error) {
      console.error('Error fetching users:', error);
      return [];
    }
  };

  // Fonction simulÃ©e pour rÃ©cupÃ©rer les messages
  const fetchMessages = async (userId) => {
    try {
      setLoading(true);

      // Simuler un dÃ©lai rÃ©seau
      await new Promise(resolve => setTimeout(resolve, 500));

      // Messages exemple
      const now = new Date();
      const oneHourAgo = new Date(now.getTime() - 3600000);
      const twoHoursAgo = new Date(now.getTime() - 7200000);

      const mockMessages = [
        {
          id: 'm1',
          sender: userId,
          receiver: currentUser?.id,
          content: 'Salut ! Comment Ã§a va ?',
          timestamp: twoHoursAgo.toISOString(),
          status: 'read',
          reactions: []
        },
        {
          id: 'm2',
          sender: currentUser?.id,
          receiver: userId,
          content: 'Ã‡a va bien, merci ! Et toi ?',
          timestamp: oneHourAgo.toISOString(),
          status: 'read',
          reactions: [
            { userId, reaction: 'ðŸ‘', timestamp: oneHourAgo.toISOString() }
          ]
        },
        {
          id: 'm3',
          sender: userId,
          receiver: currentUser?.id,
          content: 'TrÃ¨s bien ! Tu travailles sur quoi en ce moment ?',
          timestamp: now.toISOString(),
          status: 'delivered',
          reactions: []
        }
      ];

      setMessages(mockMessages);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching messages:', error);
      setLoading(false);
    }
  };

  // Envoyer un message
  const handleSendMessage = async (content) => {
    if (!selectedUser || !currentUser) return;

    try {
      const newMessage = {
        id: `m${Date.now()}`,
        sender: currentUser.id,
        receiver: selectedUser.id,
        content,
        timestamp: new Date().toISOString(),
        status: 'sent',
        reactions: []
      };

      // Ajouter le message localement
      setMessages(prevMessages => [...prevMessages, newMessage]);

      // Simuler un dÃ©lai rÃ©seau
      await new Promise(resolve => setTimeout(resolve, 300));

      // Mettre Ã  jour le statut (simulÃ©)
      setTimeout(() => {
        setMessages(prevMessages => 
          prevMessages.map(msg => 
            msg.id === newMessage.id 
              ? { ...msg, status: 'delivered' } 
              : msg
          )
        );
      }, 1000);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  // Ajouter une rÃ©action
  const handleAddReaction = async (messageId, reaction) => {
    try {
      // Mettre Ã  jour localement
      setMessages(prevMessages => 
        prevMessages.map(msg => 
          msg.id === messageId 
            ? { 
                ...msg, 
                reactions: [...(msg.reactions || []), { userId: currentUser?.id, reaction }] 
              } 
            : msg
        )
      );

      // Simuler un dÃ©lai rÃ©seau
      await new Promise(resolve => setTimeout(resolve, 200));
    } catch (error) {
      console.error('Error adding reaction:', error);
    }
  };

  // RÃ©pondre Ã  un message
  const handleReplyToMessage = async (messageId, content) => {
    try {
      if (!currentUser || !selectedUser) return;

      const parentMessage = messages.find(msg => msg.id === messageId);

      if (!parentMessage) return;

      const reply = {
        id: `m${Date.now()}`,
        sender: currentUser.id,
        receiver: selectedUser.id,
        content,
        timestamp: new Date().toISOString(),
        status: 'sent',
        parentId: messageId,
        parentMessage: {
          id: parentMessage.id,
          content: parentMessage.content
        }
      };

      // Mettre Ã  jour localement
      setMessages(prevMessages => [...prevMessages, reply]);

      // Simuler un dÃ©lai rÃ©seau
      await new Promise(resolve => setTimeout(resolve, 300));
    } catch (error) {
      console.error('Error replying to message:', error);
    }
  };

  return (
    <ChatContext.Provider
      value={{
        onlineUsers,
        fetchUsers,
        selectedUser,
        setSelectedUser,
        messages,
        loading,
        handleSendMessage,
        handleAddReaction,
        handleReplyToMessage
      }}
    >
      {children}
    </ChatContext.Provider>
  );
};
